var interval = global.get("ambientNode.parameters.ACUnitRotation.Interval");
var currUnit = global.get("ambientNode.parameters.ACUnitRotation.currentUnit");
var currUnitStartAt = global.get("ambientNode.parameters.ACUnitRotation.startAt");
var state = global.get("ambientNode.parameters.ACUnitRotation.state");
var now = new Date();

//si el dispositivo no esta en linea, no se puede hacer nada
//if(global.get("ambientNode.LWT") == "Offline" )
//  return [null,null];

//reviso si las variables existen, de lo contrario cargo valores por defecto
if(currUnitStartAt == "" || currUnitStartAt === undefined)
  global.set("ambientNode.parameters.ACUnitRotation.startAt", now.getTime());

if(currUnit == "" || currUnit === undefined) {
  if(global.get("ambientNode.state.POWER1") == "ON" && global.get("ambientNode.state.POWER2") == "OFF")  
    global.set("ambientNode.parameters.ACUnitRotation.currentUnit", 0);    
  else if(global.get("ambientNode.state.POWER1") == "OFF" && global.get("ambientNode.state.POWER2") == "ON")  
    global.set("ambientNode.parameters.ACUnitRotation.currentUnit", 1);    
  else
    global.set("ambientNode.parameters.ACUnitRotation.currentUnit", 0);    
}
if(interval == ""|| interval === undefined)
  global.set("ambientNode.parameters.ACUnitRotation.Interval", 60);
if(state == ""|| state === undefined)
  global.set("ambientNode.parameters.ACUnitRotation.state", "ENABLE");

if(!(msg.state === undefined)) {
    if(msg.state == "DISABLE") {
        global.set("ambientNode.parameters.ACUnitRotation.state", "DISABLE");
        return [null,null,null];
    } else if( msg.state == "ENABLE") {
        global.set("ambientNode.parameters.ACUnitRotation.state", "ENABLE");
        return [null,null,null];    
    }
}

// Si esta deshabilitado no hago nada
if(state == "DISABLE")
    return [null,null,null];
//calculo cuanto queda de tiempo en segundos
var delta = Math.floor(interval - (msg.payload - currUnitStartAt)/1000)
node.status(delta);

if(delta <= 0){
//si tiempo expiró actualizo contantes globales y mando comandos a cada salida
  global.set("ambientNode.parameters.ACUnitRotation.currentUnit", currUnit?0:1);    
  global.set("ambientNode.parameters.ACUnitRotation.startAt", now.getTime());

  node.log(currUnit?"Ciclando a AC0":"Ciclando a AC1");

  if(currUnit)
  //             AC1 - 0             AC2 - 1
    return [{ payload: "ON" }, { payload: "OFF" }, { payload: delta }];
  else
    return [{ payload: "OFF" }, { payload: "ON" }, { payload: delta }];
}

return [null,null, { payload: delta }];

